function toggleFolder(element) {
    const content = element.nextElementSibling;
    const expandIcon = element.querySelector('.expand-icon');
    const folderIcon = element.querySelector('.folder-icon i');
    const folderContent = element.nextElementSibling;
    // 切换内容显示状态
    if (content.classList.contains('expanded')) {
        expandIcon.classList.remove('expanded');
        folderIcon.classList.remove('fa-folder-open');
        folderIcon.classList.add('fa-folder');
        folderContent.classList.remove('expanded');
    } else {
        expandIcon.classList.add('expanded');
        folderIcon.classList.remove('fa-folder');
        folderIcon.classList.add('fa-folder-open');
        folderContent.classList.add('expanded');
    }
}


function loadNavBar() {
    $("#nav_body").empty();
    const navs = promptNavs;
    for (const nav of navs) {
        $("#nav_body").append('<div class="folder-container" contenteditable="false"></div>');
        const folder = $(".folder-container:last");
        folder.append(`
            <div class="folder-item" onclick="toggleFolder(this)">
                <div class="folder-icon">
                    <i class="fa fa-folder text-primary">
                      ${nav.icon1}
                    </i>
                </div>
                <span class="folder-text">${nav.title}</span>
                <div class="expand-icon ">
                </div>
            </div>
            `);
        folder.append('<div class="tree-line folder-content"></div>');
        const folder_content = $(".folder-content:last");
        const subNavs =  nav.subNavs;
        for (const subNav of subNavs) {
            folder_content.append(`
                <div class="file-item" onclick="${nav.bindfunc}(${nav.id}, ${subNav.id}, event)">
                    <div class="file-icon">
                        <i class="fa fa-file-code-o">
                            ${subNav.icon1}
                        </i>
                    </div>
                    <span class="file-text" >${subNav.title}</span>
                </div>
            `);
        }
    }
}

